import express from 'express';
import authentication from '../middleware/authentication.js';
import armorController from '../controllers/armorController.js';
import itemStatController from '../controllers/itemStatController.js';
const router = express.Router();
router.get('/armors', authentication.authenticate, armorController.getArmor);
router.get('/armors/:armorId', authentication.authenticate, armorController.getArmorById);
router.patch('/armors/:itemId/stats/currentPower', authentication.authenticate, itemStatController.editItemPower);
router.patch('/armors/:itemId/stats/currentBlock', authentication.authenticate, itemStatController.editItemBlock);
router.patch('/armors/:itemId/stats/refreshPower', authentication.authenticate, itemStatController.refreshItemPower);
router.patch('/armors/:itemId/stats/refreshBlock', authentication.authenticate, itemStatController.refreshItemBlock);
router.post('/armors', authentication.authenticate, authentication.authenticateAdmin, armorController.createOrUpdateArmor);
router.put('/armor/:armorId', authentication.authenticate, authentication.authenticateArmorModification, armorController.modifyArmor);
router.delete('/armor/:armorId', authentication.authenticate, authentication.authenticateAdmin, armorController.deleteArmor);
export default router;
